import React, { useState, useEffect, useRef } from 'react';

const screens = {
  START: 'start',
  DECLARATION: 'declaration',
  MEMORIES: 'memories',
  VIDEO: 'video',
};

const declarationText = `Amor, eu sei que √†s vezes posso falhar,
mas nunca deixo de tentar ser o melhor pra voc√™.

Talvez eu n√£o seja o melhor com presentes,
mas esse aqui eu fiz com todo carinho e dedica√ß√£o.

Quero que voc√™ saiba que te amo muito.
Voc√™ √© o bem mais precioso que eu tenho...

Tirando minha bicicleta, n√© ‚Äî
mas fica na paz que voc√™ tamb√©m tem um lugar especial guardado no meu cora√ß√£o.

Eu te amo muito, meu amor.
Demais mesmo, sua gostosa. üíú`;

const memoriesList = [
  'Nosso primeiro selinho no parque',
  'Dia depois do Dia dos Namorados',
  'Quando a bicicleta quebrou no parque',
  'Presentes trocados com carinho',
];

const photos = [
  'foto1.jpg',
  'foto2.jpg',
  'foto3.jpg',
  'foto4.jpg',
];

export default function App() {
  const [screen, setScreen] = useState(screens.START);
  const [typedText, setTypedText] = useState('');
  const [charIndex, setCharIndex] = useState(0);
  const [photoIndex, setPhotoIndex] = useState(0);
  const audioRef = useRef(null);

  useEffect(() => {
    if (screen === screens.DECLARATION && charIndex < declarationText.length) {
      const char = declarationText.charAt(charIndex);
      const delay = char === '\n' ? 400 : 40;
      const timeout = setTimeout(() => {
        setTypedText((prev) => prev + char);
        setCharIndex(charIndex + 1);
      }, delay);
      return () => clearTimeout(timeout);
    }
  }, [charIndex, screen]);

  useEffect(() => {
    if (screen !== screens.DECLARATION) {
      setTypedText('');
      setCharIndex(0);
    }
  }, [screen]);

  useEffect(() => {
    if (screen === screens.MEMORIES) {
      setPhotoIndex(0);
      if (audioRef.current) {
        audioRef.current.play();
      }
    } else if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
  }, [screen]);

  function nextPhoto() {
    setPhotoIndex((prev) => (prev + 1) % photos.length);
  }

  function prevPhoto() {
    setPhotoIndex((prev) => (prev - 1 + photos.length) % photos.length);
  }

  return (
    <div
      className="app"
      style={{
        backgroundImage: "url('fundo.jpg')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed'
      }}
    >
      {screen === screens.START && (
        <div className="screen start-screen active">
          <h1 className="pixacao-text">Presente pra menina mais linda do mundo</h1>
          <p className="pixacao-subtitle">Eu te amo muito meu amor, espero que goste</p>
          <button className="btn" onClick={() => setScreen(screens.DECLARATION)}>Iniciar</button>
        </div>
      )}

      {screen === screens.DECLARATION && (
        <div className="screen declaration-screen active">
          <div className="typed-text">{typedText || '\u00A0'}</div>
          {charIndex >= declarationText.length && (
            <button className="btn" onClick={() => setScreen(screens.MEMORIES)}>Ver mem√≥rias</button>
          )}
        </div>
      )}

      {screen === screens.MEMORIES && (
        <div className="screen memories-screen active">
          <h2 className="pixacao-text">Nossas mem√≥rias</h2>
          <ul>
            {memoriesList.map((mem, idx) => (
              <li key={idx}>{mem}</li>
            ))}
          </ul>

          <div className="photo-carousel">
            <button className="btn" onClick={prevPhoto} style={{ marginRight: '10px' }}>‚óÄ</button>
            <img
              src={photos[photoIndex]}
              alt={`Mem√≥ria ${photoIndex + 1}`}
              style={{ maxWidth: '300px', borderRadius: '12px', boxShadow: '0 0 15px #ff00ffaa' }}
            />
            <button className="btn" onClick={nextPhoto} style={{ marginLeft: '10px' }}>‚ñ∂</button>
          </div>

          <audio ref={audioRef} controls src="tribalistas_alianca.mp3" style={{ marginTop: '20px', width: '100%' }} />

          <button className="btn" onClick={() => setScreen(screens.VIDEO)} style={{ marginTop: '30px' }}>
            Ver v√≠deo
          </button>
        </div>
      )}

      {screen === screens.VIDEO && (
        <div className="screen video-screen active">
          <h2 className="pixacao-text">V√≠deo do casal mais lindo de todo universo</h2>
          <video controls autoPlay muted style={{ maxWidth: '100%', borderRadius: '15px', boxShadow: '0 0 15px #ff00ffaa' }}>
            <source src="video_casal.mp4" type="video/mp4" />
            Seu navegador n√£o suporta v√≠deo HTML5.
          </video>
          <button className="btn" style={{ marginTop: 20 }} onClick={() => setScreen(screens.START)}>Recome√ßar</button>
        </div>
      )}
    </div>
  );
}
