import React, { useState, useEffect } from 'react';

const screens = {
  START: 'start',
  DECLARATION: 'declaration',
  MEMORIES: 'memories',
  VIDEO: 'video',
  BRYAN_CORE: 'bryan_core',
  RIHANA_CORE: 'rihana_core',
};

const declarationText = `Veinha, eu te amo muito,  
fiz esse presente pra vc,  
posso não ser bom fazendo presente,  
mas com programação eu me garanto.  

Pode não ser o melhor presente do mundo,  
mas eu fiz com carinho e muita dedicação,  
e isso demorou meses para ser terminado.`;

const memoriesList = [
  'Nosso primeiro selinho no parque',
  'Dia depois do Dia dos Namorados',
  'Quando a bicicleta quebrou no parque',
  'Presentes trocados com carinho',
];

// Dados Bryan
const bryanData = {
  nome: 'Bryan Core',
  foto: 'bryan.jpg',           // colocar em public/
  youtuber: {
    nome: 'Youtuber do Bryan',
    foto: 'youtuber_bryan.jpg', // colocar em public/
  },
  artista: {
    nome: 'Artista do Bryan',
    foto: 'artista_bryan.jpg',  // colocar em public/
  }
};

// Dados Rihana
const rihanaData = {
  nome: 'Rihana Core',
  foto: 'rihana.jpg',            // colocar em public/
  youtuber: {
    nome: 'Youtuber da Rihana',
    foto: 'youtuber_rihana.jpg', // colocar em public/
  },
  artista: {
    nome: 'Artista da Rihana',
    foto: 'artista_rihana.jpg',  // colocar em public/
  }
};

// Componente para mostrar tela core
function CoreScreen({ data, onNext, onBack }) {
  return (
    <div className="screen core-screen active" style={{ textAlign: 'center' }}>
      <h2 className="pixacao-text">{data.nome}</h2>

      <img
        src={data.foto}
        alt={`${data.nome} foto`}
        style={{ maxWidth: '300px', borderRadius: '15px', boxShadow: '0 0 15px #ff00ffaa', margin: '20px 0' }}
      />

      <div style={{ marginBottom: 20 }}>
        <h3>Youtuber favorito</h3>
        <img
          src={data.youtuber.foto}
          alt={data.youtuber.nome}
          style={{ maxWidth: '250px', borderRadius: '15px', boxShadow: '0 0 15px #ff00ffaa' }}
        />
        <p style={{ marginTop: 10, fontWeight: 'bold', fontSize: '1.1rem' }}>{data.youtuber.nome}</p>
      </div>

      <div style={{ marginBottom: 30 }}>
        <h3>Artista / Banda favorita</h3>
        <img
          src={data.artista.foto}
          alt={data.artista.nome}
          style={{ maxWidth: '250px', borderRadius: '15px', boxShadow: '0 0 15px #ff00ffaa' }}
        />
        <p style={{ marginTop: 10, fontWeight: 'bold', fontSize: '1.1rem' }}>{data.artista.nome}</p>
      </div>

      <div>
        {onBack && <button className="btn" onClick={onBack} style={{ marginRight: 10 }}>Voltar</button>}
        {onNext && <button className="btn" onClick={onNext}>Próximo</button>}
      </div>
    </div>
  );
}

export default function App() {
  const [screen, setScreen] = useState(screens.START);
  const [typedText, setTypedText] = useState('');
  const [charIndex, setCharIndex] = useState(0);

  // Efeito digitação lenta na declaração
  useEffect(() => {
    if (screen === screens.DECLARATION && charIndex < declarationText.length) {
      const timeout = setTimeout(() => {
        setTypedText((prev) => prev + declarationText.charAt(charIndex));
        setCharIndex(charIndex + 1);
      }, 40);
      return () => clearTimeout(timeout);
    }
  }, [charIndex, screen]);

  // Resetar texto digitado ao mudar de tela
  useEffect(() => {
    if (screen !== screens.DECLARATION) {
      setTypedText('');
      setCharIndex(0);
    }
  }, [screen]);

  return (
    <div
      className="app"
      style={{
        backgroundImage: "url('fundo.jpg')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
        minHeight: '100vh',
        color: '#eee',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: 20,
      }}
    >
      {/* Tela Start */}
      {screen === screens.START && (
        <div className="screen start-screen" style={{ maxWidth: 600, background: 'rgba(30,30,30,0.85)', padding: 30, borderRadius: 15, boxShadow: '0 0 25px rgba(255,0,128,0.6)', textAlign: 'center' }}>
          <h1 className="pixacao-text" style={{ fontFamily: "'Special Elite', cursive", fontSize: '3rem', color: '#ff00ff', textShadow: '2px 2px 5px #ff00ff88' }}>
            Presente pra menina mais linda do mundo
          </h1>
          <p className="pixacao-subtitle" style={{ fontFamily: "'Special Elite', cursive", fontSize: '1.5rem', color: '#dd00ddcc', marginBottom: 30 }}>
            Eu te amo muito meu amor, espero que goste
          </p>
          <button className="btn" style={{ background: '#ff00ff', borderRadius: 8, padding: '15px 30px', fontWeight: 'bold', fontSize: '1.1rem', cursor: 'pointer', color: '#222', boxShadow: '0 0 10px #ff00ffcc' }} onClick={() => setScreen(screens.DECLARATION)}>
            Iniciar
          </button>
        </div>
      )}

      {/* Tela Declaration */}
      {screen === screens.DECLARATION && (
        <div className="screen declaration-screen" style={{ maxWidth: 600, background: 'rgba(30,30,30,0.85)', padding: 30, borderRadius: 15, boxShadow: '0 0 25px rgba(255,0,128,0.6)', color: '#eee', minHeight: 150 }}>
          <div className="typed-text" style={{ fontSize: '1.3rem', whiteSpace: 'pre-wrap', lineHeight: 1.4, minHeight: 120, marginBottom: 15 }}>
            {typedText || '\u00A0'}
          </div>
          {charIndex >= declarationText.length && (
            <button className="btn" onClick={() => setScreen(screens.MEMORIES)} style={{ background: '#ff00ff', borderRadius: 8, padding: '15px 30px', fontWeight: 'bold', fontSize: '1.1rem', cursor: 'pointer', color: '#222', boxShadow: '0 0 10px #ff00ffcc' }}>
              Ver memórias
            </button>
          )}
        </div>
      )}

      {/* Tela Memories */}
      {screen === screens.MEMORIES && (
        <div className="screen memories-screen" style={{ maxWidth: 600, background: 'rgba(30,30,30,0.85)', padding: 30, borderRadius: 15, boxShadow: '0 0 25px rgba(255,0,128,0.6)', color: '#eee', textAlign: 'left' }}>
          <h2 className="pixacao-text" style={{ fontFamily: "'Special Elite', cursive", fontSize: '2rem', color: '#ff00ff', marginBottom: 20 }}>Nossas memórias</h2>
          <ul>
            {memoriesList.map((mem, idx) => (
              <li key={idx} style={{ marginBottom: 10, fontSize: '1.2rem' }}>{mem}</li>
            ))}
          </ul>
          <button className="btn" onClick={() => setScreen(screens.VIDEO)} style={{ background: '#ff00ff', borderRadius: 8, padding: '15px 30px', fontWeight: 'bold', fontSize: '1.1rem', cursor: 'pointer', color: '#222', boxShadow: '0 0 10px #ff00ffcc' }}>
            Ver vídeo
          </button>
        </div>
      )}

      {/* Tela Vídeo */}
      {screen === screens.VIDEO && (
        <div className="screen video-screen" style={{ maxWidth: 600, background: 'rgba(30,30,30,0.85)', padding: 30, borderRadius: 15, boxShadow: '0 0 25px rgba(255,0,128,0.6)', color: '#eee', textAlign: 'center' }}>
          <h2 className="pixacao-text" style={{ fontFamily: "'Special Elite', cursive", fontSize: '2rem', color: '#ff00ff', marginBottom: 20 }}>
            Vídeo do casal mais lindo de todo universo
          </h2>
          <video controls autoPlay muted style={{ maxWidth: '100%', borderRadius: '15px', boxShadow: '0 0 15px #ff00ffaa' }}>
            <source src="video_casal.mp4" type="video/mp4" />
            Seu navegador não suporta vídeo HTML5.
          </video>
          <button className="btn" onClick={() => setScreen(screens.BRYAN_CORE)} style={{ marginTop: 20, background: '#ff00ff', borderRadius: 8, padding: '15px 30px', fontWeight: 'bold', fontSize: '1.1rem', cursor: 'pointer', color: '#222', boxShadow: '0 0 10px #ff00ffcc' }}>
            Próxima
          </button>
        </div>
      )}

      {/* Tela Bryan Core */}
      {screen === screens.BRYAN_CORE && (
        <CoreScreen
          data={bryanData}
          onNext={() => setScreen(screens.RIHANA_CORE)}
          onBack={() => setScreen(screens.VIDEO)}
        />
      )}

      {/* Tela Rihana Core */}
      {screen === screens.RIHANA_CORE && (
        <CoreScreen
          data={rihanaData}
          onBack={() => setScreen(screens.BRYAN_CORE)}
          onNext={() => setScreen(screens.START)}
        />
      )}
    </div>
  );
}
