}

export default function App() {
  const [screen, setScreen] = useState(screens.START);
  const [typedText, setTypedText] = useState('');
  const [charIndex, setCharIndex] = useState(0);
  const [photoIndex, setPhotoIndex] = useState(0);
  const audioDeclarationRef = useRef(null);
  const audioMemoriesRef = useRef(null);

  useEffect(() => {
    if (screen === screens.DECLARATION && charIndex < declarationText.length) {
      const char = declarationText.charAt(charIndex);
      const delay = char === '\n' ? 400 : 40;
      const timeout = setTimeout(() => {
        setTypedText((prev) => prev + char);
        setCharIndex((i) => i + 1);
      }, delay);
      return () => clearTimeout(timeout);
    }
  }, [charIndex, screen]);

  useEffect(() => {
    if (screen !== screens.DECLARATION) {
      setTypedText('');
      setCharIndex(0);
    }
  }, [screen]);

  useEffect(() => {
    if (screen === screens.MEMORIES) {
      setPhotoIndex(0);
      audioMemoriesRef.current?.play();
    } else {
      audioMemoriesRef.current?.pause();
      if (audioMemoriesRef.current) audioMemoriesRef.current.currentTime = 0;
    }
  }, [screen]);

  useEffect(() => {
    if (screen === screens.DECLARATION) {
      audioDeclarationRef.current?.play();
    } else {
      audioDeclarationRef.current?.pause();
      if (audioDeclarationRef.current) audioDeclarationRef.current.currentTime = 0;
    }
  }, [screen]);

  const nextPhoto = () => setPhotoIndex((i) => (i + 1) % photos.length);
  const prevPhoto = () => setPhotoIndex((i) => (i - 1 + photos.length) % photos.length);

  function launchConfetti() {
    confetti({ particleCount: 150, spread: 120, origin: { y: 0.6 } });
  }

  return (
    <div
      className="app"
      style={{
        background: `linear-gradient(135deg, rgba(106,13,173,0.7), rgba(187,77,233,0.3)), url('/fundo.jpg') center/cover no-repeat`,
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: 20,
      }}
    >
      {screen === screens.START && (
        <div className="screen start-screen active">
          <h1 className="pixacao-text">Presente pra menina mais linda do mundo</h1>
          <p className="pixacao-subtitle">Eu te amo muito meu amor, espero que goste</p>
          <button className="btn" onClick={() => setScreen(screens.DECLARATION)}>
            Iniciar
          </button>
        </div>
      )}

      {screen === screens.DECLARATION && (
        <div className="screen declaration-screen active">
          <audio ref={audioDeclarationRef} src="neguinho_do_kaxeta.mp3" />
          <div className="typed-text">{typedText || '\u00A0'}</div>
          {charIndex >= declarationText.length && (
            <button className="btn" onClick={() => setScreen(screens.MEMORIES)}>
              Ver memórias
            </button>
          )}
        </div>
      )}

      {screen === screens.MEMORIES && (
        <div className="screen memories-screen active">
          <h2 className="pixacao-text">Nossas memórias</h2>
          <ul style={{ textAlign: 'left', marginBottom: 20 }}>{memoriesList.map((m, i) => <li key={i}>{m}</li>)}</ul>
          <div className="photo-carousel">
            <button className="btn" onClick={prevPhoto}>
              ◀
            </button>
            <img src={photos[photoIndex]} alt="" style={{ maxWidth: 300, margin: '0 10px', borderRadius: 12 }} />
            <button className="btn" onClick={nextPhoto}>
              ▶
            </button>
          </div>
          <audio ref={audioMemoriesRef} controls src="tribalistas_alianca.mp3" style={{ width: '100%', marginTop: 20 }} />
          <button className="btn" onClick={() => setScreen(screens.VIDEO)} style={{ marginTop: 30 }}>
            Ver vídeo
          </button>
        </div>
      )}

      {screen === screens.VIDEO && (
        <div className="screen video-screen active">
          <h2 className="pixacao-text">Vídeo do casal mais lindo de todo universo</h2>
          <video controls autoPlay muted style={{ maxWidth: '100%', borderRadius: 15 }}>
            <source src="video_casal.mp4" type="video/mp4" />
          </video>
          <button className="btn" onClick={() => setScreen(screens.BRYAN_CORE)} style={{ marginTop: 20 }}>
            Próxima
          </button>
        </div>
      )}

      {screen === screens.BRYAN_CORE && (
        <CoreScreen data={bryanData} onBack={() => setScreen(screens.VIDEO)} onNext={() => setScreen(screens.RIHANA_CORE)} />
      )}

      {screen === screens.RIHANA_CORE && (
        <div>
          <CoreScreen data={rihanaData} onBack={() => setScreen(screens.BRYAN_CORE)} onNext={null} />
          <div style={{ textAlign: 'center', marginTop: 20 }}>
            <button className="btn" onClick={() => setScreen(screens.SECRET)}>
              ......
            </button>
          </div>
        </div>
      )}

      {screen === screens.SECRET && (
        <div className="screen core-screen active" style={{ textAlign: 'center' }}>
          <h2 className="pixacao-text">🕵️ Tela Secreta</h2>
          <p style={{ fontSize: '1.3rem', marginBottom: '30px' }}>Para liberar essa tela... mande a foto do peito 👀</p>
          <button className="btn" onClick={() => setScreen(screens.LOVE)}>
            Prosseguir
          </button>
        </div>
      )}

      {screen === screens.LOVE && (
        <div className="screen core-screen active" style={{ textAlign: 'center' }}>
          <h2 className="pixacao-text">É brincadeira meu amoooor</h2>
          <p style={{ fontSize: '1.4rem', marginBottom: '30px' }}>Eu te amo muito 💜</p>
          <h3 style={{ fontSize: '1.8rem' }}>✨ FIM ✨</h3>
          {launchConfetti()}
        </div>
      )}
    </div>
  );
}
