import React, { useState, useEffect, useRef } from 'react';

const screens = {
  START: 'start',
  DECLARATION: 'declaration',
  MEMORIES: 'memories',
  VIDEO: 'video',
};

const declarationText = `Amor, eu sei que Ã s vezes posso falhar,
mas nunca deixo de tentar ser o melhor pra vocÃª.

Talvez eu nÃ£o seja o melhor com presentes,
mas esse aqui eu fiz com todo carinho e dedicaÃ§Ã£o.

Quero que vocÃª saiba que te amo muito.
VocÃª Ã© o bem mais precioso que eu tenho...

Tirando minha bicicleta, nÃ© â€”
mas fica na paz que vocÃª tambÃ©m tem um lugar especial guardado no meu coraÃ§Ã£o.

Eu te amo muito, meu amor.
Demais mesmo, sua gostosa. ðŸ’œ`;

const memoriesList = [
  'Nosso primeiro selinho no parque',
  'Dia depois do Dia dos Namorados',
  'Quando a bicicleta quebrou no parque',
  'Presentes trocados com carinho',
];

// Substitua as URLs das fotos pelos seus arquivos locais ou links
const photos = [
  'foto1.jpg',
  'foto2.jpg',
  'foto3.jpg',
  'foto4.jpg',
];

export default function App() {
  const [screen, setScreen] = useState(screens.START);
  const [typedText, setTypedText] = useState('');
  const [charIndex, setCharIndex] = useState(0);
  const [photoIndex, setPhotoIndex] = useState(0);
  const audioRef = useRef(null);

  // DigitaÃ§Ã£o lenta com pausas nas quebras de linha
  useEffect(() => {
    if (screen === screens.DECLARATION && charIndex < declarationText.length) {
      const char = declarationText.charAt(charIndex);
      const delay = char === '\n' ? 400 : 40;
      const timeout = setTimeout(() => {
        setTypedText((prev) => prev + char);
        setCharIndex(charIndex + 1);
      }, delay);
      return () => clearTimeout(timeout);
    }
  }, [charIndex, screen]);

  // Resetar digitaÃ§Ã£o ao sair da tela de declaraÃ§Ã£o
  useEffect(() => {
    if (screen !== screens.DECLARATION) {
      setTypedText('');
      setCharIndex(0);
    }
  }, [screen]);

  // Resetar fotos e tocar mÃºsica ao entrar na tela de memÃ³rias
  useEffect(() => {
    if (screen === screens.MEMORIES) {
      setPhotoIndex(0);
      if (audioRef.current) {
        audioRef.current.play();
      }
    } else if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
  }, [screen]);

  // AvanÃ§ar foto manualmente
  function nextPhoto() {
    setPhotoIndex((prev) => (prev + 1) % photos.length);
  }
  function prevPhoto() {
    setPhotoIndex((prev) => (prev - 1 + photos.length) % photos.length);
  }

  return (
    <div className="app">
      <div className={`screen start-screen ${screen === screens.START ? 'active' : ''}`}>
        <h1 className="pixacao-text">Presente pra menina mais linda do mundo</h1>
        <p className="pixacao-subtitle">Eu te amo muito meu amor, espero que goste</p>
        <button className="btn" onClick={() => setScreen(screens.DECLARATION)}>Iniciar</button>
      </div>

      <div className={`screen declaration-screen ${screen === screens.DECLARATION ? 'active' : ''}`}>
        <div className="typed-text">{typedText || '\u00A0'}</div>
        {charIndex >= declarationText.length && (
          <button className="btn" onClick={() => setScreen(screens.MEMORIES)}>Ver memÃ³rias</button>
        )}
      </div>

      <div className={`screen memories-screen ${screen === screens.MEMORIES ? 'active' : ''}`}>
        <h2 className="pixacao-text">Nossas memÃ³rias</h2>
        <ul>
          {memoriesList.map((mem, idx) => (
            <li key={idx}>{mem}</li>
          ))}
        </ul>

        <div className="photo-carousel">
          <button className="btn" onClick={prevPhoto} style={{ marginRight: '10px' }}>â—€</button>
          <img
            src={photos[photoIndex]}
            alt={`MemÃ³ria ${photoIndex + 1}`}
            style={{ maxWidth: '300px', borderRadius: '12px', boxShadow: '0 0 15px #ff00ffaa' }}
          />
          <button className="btn" onClick={nextPhoto} style={{ marginLeft: '10px' }}>â–¶</button>
        </div>

        <audio ref={audioRef} controls src="tribalistas_alianca.mp3" style={{ marginTop: '20px', width: '100%' }} />

        <button className="btn" onClick={() => setScreen(screens.VIDEO)} style={{ marginTop: '30px' }}>
          Ver vÃ­deo
        </button>
      </div>

      <div className={`screen video-screen ${screen === screens.VIDEO ? 'active' : ''}`}>
        <h2 className="pixacao-text">VÃ­deo do casal mais lindo de todo universo</h2>
        <video controls autoPlay muted style={{ maxWidth: '100%', borderRadius: '15px', boxShadow: '0 0 15px #ff00ffaa' }}>
          <source src="video_casal.mp4" type="video/mp4" />
          Seu navegador nÃ£o suporta vÃ­deo HTML5.
        </video>
        <button className="btn" style={{ marginTop: 20 }} onClick={() => setScreen(screens.START)}>RecomeÃ§ar</button>
      </div>
    </div>
  );
}
